interface NavigationMenuProps {
  menuItems?: MenuItem[];
}

export default function NavigationMenu({
  menuItems = defaultMenuItems,
}: NavigationMenuProps) {
  const { user } = useAuth();
  const pathname = usePathname();
  const { isMobile } = useDevice();

  // Фильтруем пункты меню в зависимости от роли пользователя
  const filteredMenuItems = menuItems.filter((item) => {
    // Если у пункта нет ограничений по ролям, показываем его всем
    if (!item.roles || item.roles.length === 0) {
      return true;
    }
    // Если есть ограничения, проверяем роль пользователя
    return user?.role && item.roles.includes(user.role);
  });

  // Функция для проверки активности пункта меню
  const isActiveItem = (href: string) => {
    // Точное совпадение для главной страницы
    if (href === "/") {
      return pathname === "/";
    }
    // Для остальных страниц проверяем начало пути
    return pathname.startsWith(href);
  };

  return (
    <nav
      className={cn("px-4 pt-2")}
      style={{
        paddingBottom: isMobile ? "29px" : "8px",
      }}
    >
      <ul
        className={cn(
          "flex justify-between",
          user?.role === "ADMIN" ? "px-2" : "px-5"
        )}
      >
        {filteredMenuItems.map((item) => {
          const isActive = isActiveItem(item.href);

          return (
            <li
              key={item.id}
              className={cn(
                "font-medium w-fit grid place-items-center gap-1 text-sm",
                isActive ? "text-white" : "text-white/80"
              )}
            >
              <Link
                href={item.href}
                onClick={() => hapticFeedback("light")}
                className={cn(
                  "size-[50px] grid place-items-center transition-all duration-200",
                  isActive ? "menu-btn-active" : "menu-btn"
                )}
              >
                {item.icon}
              </Link>
              {item.label}
            </li>
          );
        })}
      </ul>
    </nav>
  );
}
